import React, { useEffect } from "react";
import Loading from "./Loading";
import { Map, TileLayer } from "react-leaflet";
import "../../css/app.css";
import { useSelector, useDispatch } from "react-redux";
import { getData } from "../features/markerSlice";
import { Curve } from 'react-leaflet-curve'

export const LeafMap = () => {
    const dispatch = useDispatch();

    const path = ['M', [50.14874640066278, 14.106445312500002],
  'Q', [51.67255514839676, 16.303710937500004],
  [50.14874640066278, 18.676757812500004],
  'T', [49.866316729538674, 25.0927734375]]

    // state
    const stateMarker = useSelector(state => state.marker);
    const stateMap = useSelector(state => state.map);

    // Effects
    useEffect(() => {
        dispatch(getData(stateMarker.endpoint));
    }, [dispatch]);

    // render component
    if (stateMarker.data.length > 0) {
        return (
            <Map doubleClickZoom={false} preferCanvas={true} center={[stateMap.lat, stateMap.lng]}
            zoom={stateMap.zoom} scrollWheelZoom={true}>
              <TileLayer
                url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Street_Map/MapServer/tile/{z}/{y}/{x}"
              />
              <Curve positions={path} option={{color:'red',fill:true}}/>
              <Loading />
            </Map>
        );
    } else {
        return (
            <Map center={[stateMap.lat, stateMap.lng]} zoom={stateMap.zoom} scrollWheelZoom={true}>
              <TileLayer
                url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Street_Map/MapServer/tile/{z}/{y}/{x}"
              />
            </Map>
        );
    }

}
