import { useLeaflet } from "react-leaflet";
import '@elfalem/leaflet-curve'
import { useDispatch, useSelector } from "react-redux";
import { makeStyles } from '@material-ui/core/styles';

import { setFilteredSpecies } from "../features/markerSlice";

import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';

const useStyles = makeStyles((theme) => ({
  autocomplete: {
    margin: theme.spacing(1),
    top: theme.spacing(8),
    right: theme.spacing(1),
    position: 'fixed',
    backgroundColor: 'white',
    zIndex: 1000,
  }
}));

const FilterControls = () => {
    const { map } = useLeaflet();
    const classes = useStyles();
    const dispatch = useDispatch();
    const stateMarker = useSelector(state => state.marker);
    const species_array = [... new Set(stateMarker.data.map(a => a.species))];

    var species = [];
    for (var i = 0; i<species_array.length; i++) {
        species.push({"species":species_array[i]});
    }

    function setSpecies(species) {
        map.eachLayer(function(layer) {
            if (layer._path != null) {
                console.log(layer._path)
                if (layer._path.getAttribute("class") == "leaflet-interactive") {
                    if(layer._path.getAttribute("fill-rule") != "evenodd") {map.removeLayer(layer);}
                }
            }
        });
        dispatch(setFilteredSpecies(species));
    }

    return (
        <Autocomplete
          id="species"
          size="small"
          onChange={(event, newValue) => { if (newValue != null) {setSpecies(newValue.species);}}}
          defaultValue={species[0]}
          options={species}
          className={classes.autocomplete}
          getOptionLabel={(option) => option.species}
          style={{ width: 250 }}
          renderInput={(params) => <TextField {...params} label="species" variant="outlined" />}
        />
    );

}

export default FilterControls;
