import React, { useEffect } from "react";
import Grid from '@material-ui/core/Grid';
import "../../css/app.css";
import { useSelector, useDispatch } from "react-redux";
import { getData, sendData } from "../features/gameSlice";
import TextField from '@material-ui/core/TextField';

export const Game = () => {
    const dispatch = useDispatch();

    // state
    const gameState = useSelector(state => state.data);

    function keyPress(e) {
        if(e.keyCode === 13){
            if (e.target.value !== "") {
                var id = e.target.id;
                var room = 1;
                var endpoint = '/api/get/sendgame?player='+id+"&room="+room;
                dispatch(sendData(endpoint));
            }
        }
    }

    // Effects
    /*useEffect(() => {
        dispatch(getData(gameState.endpoint));
    }, [dispatch]);*/

    // render component
        return (
            <Grid container justify="center" alignItems="center" spacing={2}>
                <Grid item align="center" xs={3} >
                    Player1
                    <TextField id="player1" onKeyDown={keyPress} label="Outlined" variant="outlined" />
                </Grid>
                <Grid item align="center" xs={6} >
                    Game
                </Grid>
                <Grid item align="center" xs={3} >
                    Player2
                    <TextField id="player2" onKeyDown={keyPress} label="Outlined" variant="outlined" />
                </Grid>
            </Grid>
        );

}
