import React, { useEffect } from "react";
import Loading from "./Loading";
import { Map, TileLayer } from "react-leaflet";
import "../../css/app.css";
import { useSelector, useDispatch } from "react-redux";
import { getData } from "../features/markerSlice";
import { Curve } from 'react-leaflet-curve'

export const LeafMap = () => {
    const dispatch = useDispatch();

    const pathTwo = ['M', [50.54136296522163, 28.520507812500004],
  'C', [52.214338608258224, 28.564453125000004],
  [48.45835188280866, 33.57421875000001],
  [50.680797145321655, 33.83789062500001],
  'V', [48.40003249610685],
  'L', [47.45839225859763, 31.201171875],
  [48.40003249610685, 28.564453125000004], 'Z',
  'M', [49.55372551347579, 29.465332031250004],
  'V', [48.7822260446217],
  'H', [33.00292968750001],
  'V', [49.55372551347579], 'Z']

    // state
    const stateMarker = useSelector(state => state.marker);
    const stateMap = useSelector(state => state.map);

    // Effects
    useEffect(() => {
        dispatch(getData(stateMarker.endpoint));
    }, [dispatch]);

    // render component
    if (stateMarker.data.length > 0) {
        return (
            <MapContainer doubleClickZoom={false} preferCanvas={true} center={[stateMap.lat, stateMap.lng]}
            zoom={stateMap.zoom} scrollWheelZoom={true}>
              <TileLayer
                url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Street_Map/MapServer/tile/{z}/{y}/{x}"
              />
              <Curve positions={pathTwo} option={{color:'red',fill:true}}/>
              <Loading />
            </MapContainer>
        );
    } else {
        return (
            <MapContainer center={[stateMap.lat, stateMap.lng]} zoom={stateMap.zoom} scrollWheelZoom={true}>
              <TileLayer
                url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Street_Map/MapServer/tile/{z}/{y}/{x}"
              />
              <Curve positions={pathTwo} option={{color:'red',fill:true}}/>
            </MapContainer>
        );
    }

}
