import { useLeaflet } from "react-leaflet";
import L from 'leaflet'
import '@elfalem/leaflet-curve'
import { useDispatch, useSelector, batch } from "react-redux";
import { makeStyles } from '@material-ui/core/styles';

import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';

const useStyles = makeStyles((theme) => ({
  autocomplete: {
    margin: theme.spacing(1),
    top: theme.spacing(8),
    right: theme.spacing(1),
    position: 'fixed',
    backgroundColor: 'white',
    zIndex: 1000,
  }
}));

const FilterControls = () => {
    const classes = useStyles();
    const dispatch = useDispatch();
    const stateMarker = useSelector(state => state.marker);
    const species_array = [... new Set(stateMarker.data.map(a => a.species))];
    var species = [];
    for (var i = 0; i<species_array.length; i++) {
        species.push({"species":species_array[i]});
    }
    console.log(species);

    return (
        <Autocomplete
          id="species"
          size="small"
          defaultValue={species[0]}
          options={species}
          className={classes.autocomplete}
          getOptionLabel={(option) => option.species}
          style={{ width: 250 }}
          renderInput={(params) => <TextField {...params} label="species" variant="outlined" />}
        />
    );

}

export default FilterControls;
