import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';

// CREATE Thunk
export const getData = createAsyncThunk("markers/getData", async (endpoint, thunkAPI) => {
    try {
        const response = await axios.get(endpoint);
        return response.data;
    } catch (error) {
         return thunkAPI.rejectWithValue({ error: error.message });
    }
});


// CREATE SLICE
const markerSlice = createSlice({
  name: "markers",
  initialState: {
    data: [],
    filtered_data: [],
    filtered_species: '',
    endpoint: "/api/get/data",
    loading: false,
  },
  reducers: {
    setEndpoint: (state, action) => {state.endpoint = "/api/get/data"},
    setFilteredSpecies: (state, action) => {
        state.filtered_species = action.payload;
        state.filtered_data = state.data.filter(function(e) {return e.species == state.filtered_species;})
    },
  },
  extraReducers: (builder) => {
    // global
    builder.addCase(getData.pending, (state) => {
        state.data = [];
    });
    builder.addCase(getData.fulfilled, (state, { payload }) => {
        state.data = payload;
        //state.filtered_species = state.data[0]['species'];
        //state.filtered_data = state.data.filter(function(e) {return e.species == state.filtered_species;})
        state.filtered_data = state.data;
    });
    builder.addCase(getData.rejected,(state, action) => {
        state.loading = false;
    });
  }
});

export const { setEndpoint, setFilteredSpecies } = markerSlice.actions;

export default markerSlice
