import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { createNewGame, getNewGame } from "../features/gameSlice";
import Button from '@material-ui/core/Button';

const CreateGame = () => {
    const dispatch = useDispatch();

    // state
    const gameState = useSelector(state => state.game);

    // Get Open Games
    useEffect(() => {
      const interval = setInterval(() => {
          if (gameState.game_created === true) {
            dispatch(getNewGame("/api/get/newgame"));
          }
      }, 2000);
      return () => clearInterval(interval);
    }, []);

    // create new game
    function createNewGame() {
        var player1 = gameState.game_waiting_data['player1_lastseen']
        var player2 = gameState.game_waiting_data['player2_lastseen']

        console.log(player1)

        if (player1 && player2) {
            console.log("test")
        }
        //dispatch(getNewGame('/api/get/creategame'));
    }

    // render component
    if (gameState.game_waiting === true) {
        return (
            null
        );
    } else {
        return(
            <Button variant="contained" color="primary" onClick={createNewGame}>
                Enter New Game
            </Button>
        )
    }
}

export default CreateGame;
